CREATE TABLE "Contrato" ( `ContratoId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `VolumeDisponivel` NUMERIC NOT NULL, `DataInicioVigencia` INTEGER NOT NULL, `DataFimVigencia` INTEGER NOT NULL, `Ativo` INTEGER NOT NULL )
CREATE TABLE "Pedido" ( `PedidoId` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, `ContratoId` INTEGER NOT NULL, `Volume` NUMERIC NOT NULL, `DataPedido` INTEGER NOT NULL )